        "latin_14": "Bali-insaanu 'ala nafsihi bashiiratun",
        "latin_15": "Walau alqa ma'aadziirah(u)",
        "latin_16": "Laa tuharrik bihi lisaanaka lita'jala bih(i)",
        "latin_17": "Inna 'alainaa jam'ahu waquraanah(u)",
        "latin_18": "Fa-idzaa qara'naahu faattabi' quraanah(u)",
        "latin_19": "Tsumma inna 'alainaa bayaanah(u)",
        "latin_20": "Kalaa bal tuhibbuunal 'aajilat(a)",
        "latin_21": "Watadzaruuna-aakhirat(a)",
        "latin_22": "Wujuuhun yauma-idzin naadhiratun",
        "latin_23": "Ila rabbihaa naazhiratun",
        "latin_24": "Wawujuuhun yauma-idzin baasiratun",
        "latin_25": "Tazhunnu an yuf'ala bihaa faaqiratun",
        "latin_26": "Kalaa idzaa balaghatittaraaqiy(a)",
        "latin_27": "Waqiila man raaqin",
        "latin_28": "Wazhanna annahul firaaq(u)",
        "latin_29": "Wal taffatissaaqu bissaaq(i)",
        "latin_30": "Ila rabbika yauma-idzil masaaq(u)",
        "latin_31": "Falaa shaddaqa walaa shall(a)",
        "latin_32": "Walakin kadz-dzaba watawall(a)",
        "latin_33": "Tsumma dzahaba ila ahlihi yatamath-th(a)",
        "latin_34": "Aula laka fa-aul(a)",
        "latin_35": "Tsumma aula laka fa-aul(a)",
        "latin_36": "Ayahsabu-insaanu an yutraka sudan",
        "latin_37": "Alam yaku nuthfatan min manii-yin yumn(a)",
        "latin_38": "Tsumma kaana 'alaqatan fakhalaqa fasau-w(a)",
        "latin_39": "Faja'ala minhuzzaujainidz-dzakara wal ants(a)",
        "latin_40": "Alaisa dzalika biqaadirin 'ala an yuhyiyal maut(a)"
